import React, { useState, useRef } from "react";
import { Card, CardContent, Typography, Chip, Box, Link, IconButton } from "@mui/material";
import VolumeUpIcon from "@mui/icons-material/VolumeUp";
import VolumeOffIcon from "@mui/icons-material/VolumeOff";

export default function RedditPostCard({ post }) {
  const [muted, setMuted] = useState(true);
  const videoRef = useRef(null);

  const toggleMute = () => {
    if (videoRef.current) {
      videoRef.current.muted = !muted;
      setMuted(!muted);
    }
  };

  return (
    <Card sx={{ mb: 3, borderRadius: 3, boxShadow: 4 }}>
      <CardContent>
        <Typography variant="subtitle2" color="text.secondary">
          {post.author ? `u/${post.author} • ` : ""}
          {new Date(post.scraped_at).toLocaleString()}
        </Typography>

        <Typography variant="h6" gutterBottom>
          {post.title}
        </Typography>

        {post.tag && <Chip label={post.tag} size="small" sx={{ mb: 1 }} />}

        <Box mt={1}>
          <Link
            href={post.link}
            target="_blank"
            rel="noopener noreferrer"
            underline="hover"
            sx={{ wordBreak: "break-word", color: "primary.main" }}
          >
            {post.link}
          </Link>
        </Box>

        {post.video_url ? (
          <Box mt={2} sx={{ position: "relative" }}>
            <video
              ref={videoRef}
              controls
              muted={muted}
              src={post.video_url}
              style={{
                width: "100%",
                maxHeight: 480,
                borderRadius: 8,
              }}
              referrerPolicy="no-referrer"
            />
            {post.has_audio && (
              <IconButton
                onClick={toggleMute}
                sx={{
                  position: "absolute",
                  top: 10,
                  right: 10,
                  backgroundColor: "rgba(0,0,0,0.5)",
                  color: "white",
                  "&:hover": { backgroundColor: "rgba(0,0,0,0.7)" },
                }}
              >
                {muted ? <VolumeOffIcon /> : <VolumeUpIcon />}
              </IconButton>
            )}
          </Box>
        ) : post.image_url ? (
          <Box mt={2}>
            <img
              src={post.image_url}
              alt={post.title || "Reddit post image"}
              style={{
                width: "100%",
                maxHeight: 400,
                objectFit: "cover",
                borderRadius: 8,
              }}
              referrerPolicy="no-referrer"
            />
          </Box>
        ) : null}
      </CardContent>
    </Card>
  );
}
