import React from "react";
import { Card, CardContent, Typography, Chip, Box, Link } from "@mui/material";

export default function RedditPostCard({ post }) {
  // Extract Reddit post ID from the URL for embedding
  const redditEmbedUrl = post.link?.includes("reddit.com")
    ? `https://www.redditmedia.com${post.link.replace("https://reddit.com", "")}?ref_source=embed&ref=share&embed=true`
    : null;

  return (
    <Card sx={{ mb: 3, borderRadius: 3, boxShadow: 4 }}>
      <CardContent>
        <Typography variant="subtitle2" color="text.secondary">
          {post.author ? `u/${post.author} • ` : ""}
          {new Date(post.scraped_at).toLocaleString()}
        </Typography>

        <Typography variant="h6" gutterBottom>
          {post.title}
        </Typography>

        {post.tag && (
          <Chip label={post.tag} size="small" sx={{ mb: 1 }} />
        )}

        <Box mt={1}>
          <Link
            href={post.link}
            target="_blank"
            rel="noopener noreferrer"
            underline="hover"
            sx={{ wordBreak: "break-word", color: "primary.main" }}
          >
            {post.link}
          </Link>
        </Box>

        {/* Use Reddit Embed if it's a video post */}
        {post.video_url ? (
  <Box mt={2} sx={{ position: 'relative' }}>
    <video
      controls
      src={post.video_url}
      crossOrigin="anonymous"
      style={{
        width: "100%",
        maxHeight: 480,
        borderRadius: 8,
      }}
    >
      Your browser does not support the video tag.
    </video>
      </Box>
    ) : post.image_url ? (
      <Box mt={2}>
        <img
          src={post.image_url}
          alt={post.title || "Reddit post image"}
          style={{
            width: "100%",
            maxHeight: 400,
            objectFit: "cover",
            borderRadius: 8,
          }}
          referrerPolicy="no-referrer"
        />
      </Box>
    ) : null}
      </CardContent>
    </Card>
  );
}
