import React, { useRef, useEffect } from "react";
import { Card, CardContent, Typography, Chip, Box, Link } from "@mui/material";

export default function RedditPostCard({ post }) {
  const videoRef = useRef(null);
  const audioRef = useRef(null);

  useEffect(() => {
    if (videoRef.current && audioRef.current) {
      const video = videoRef.current;
      const audio = audioRef.current;

      // Sync playback and pause
      const handlePlay = () => {
        if (audio.paused) audio.play();
      };

      const handlePause = () => {
        if (!audio.paused) audio.pause();
      };

      video.addEventListener("play", handlePlay);
      video.addEventListener("pause", handlePause);

      return () => {
        video.removeEventListener("play", handlePlay);
        video.removeEventListener("pause", handlePause);
      };
    }
  }, []);

  return (
    <Card sx={{ mb: 3, borderRadius: 3, boxShadow: 4 }}>
      <CardContent>
        <Typography variant="subtitle2" color="text.secondary">
          {post.author ? `u/${post.author} • ` : ""}
          {new Date(post.scraped_at).toLocaleString()}
        </Typography>

        <Typography variant="h6" gutterBottom>
          {post.title}
        </Typography>

        {post.tag && (
          <Chip label={post.tag} size="small" sx={{ mb: 1 }} />
        )}

        <Box mt={1}>
          <Link
            href={post.link}
            target="_blank"
            rel="noopener noreferrer"
            underline="hover"
            sx={{ wordBreak: "break-word", color: "primary.main" }}
          >
            {post.link}
          </Link>
        </Box>

        {/* Reddit-hosted video with audio workaround */}
        {post.video_url ? (
          <Box mt={2}>
            <video
              ref={videoRef}
              controls
              style={{ width: "100%", borderRadius: 8 }}
              muted={!post.audio_url}
              crossOrigin="anonymous"
            >
              <source src={post.video_url} type="video/mp4" />
              Your browser does not support the video tag.
            </video>
            {/* Separate audio tag (hidden) for Reddit DASH_audio */}
            {post.audio_url && (
              <audio
                ref={audioRef}
                src={post.audio_url}
                preload="auto"
                crossOrigin="anonymous"
              />
            )}
          </Box>
        ) : post.image_url ? (
          <Box mt={2}>
            <img
              src={post.image_url}
              alt={post.title || "Reddit post image"}
              style={{ width: "100%", maxHeight: 400, objectFit: "cover", borderRadius: 8 }}
              referrerPolicy="no-referrer"
            />
          </Box>
        ) : null}
      </CardContent>
    </Card>
  );
}
