import React from "react";
import { Card, CardContent, Typography, Chip, Box, Link } from "@mui/material";

export default function RedditPostCard({ post }) {
  return (
    <Card sx={{ mb: 3, borderRadius: 3, boxShadow: 4 }}>
      <CardContent>
        <Typography variant="subtitle2" color="text.secondary">
          {post.author ? `u/${post.author} • ` : ""}
          {new Date(post.scraped_at).toLocaleString()}
        </Typography>

        <Typography variant="h6" gutterBottom>
          {post.title}
        </Typography>

        {post.tag && (
          <Chip label={post.tag} size="small" sx={{ mb: 1 }} />
        )}

        <Box mt={1}>
          <Link
            href={post.link}
            target="_blank"
            rel="noopener noreferrer"
            underline="hover"
            sx={{ wordBreak: "break-word", color: "primary.main" }}
          >
            {post.link}
          </Link>
        </Box>

        {/* Prefer video if available, else image */}
        
      </CardContent>
    </Card>
  );
}
