import React from "react";
import { Card, CardContent, Typography, Chip, Box, Link } from "@mui/material";

export default function RedditPostCard({ post }) {
  const redditEmbedUrl = post.link?.includes("reddit.com")
    ? `https://www.redditmedia.com${post.link.replace("https://reddit.com", "")}?ref_source=embed&ref=share&embed=true`
    : null;

  return (
    <Card sx={{ mb: 3, borderRadius: 3, boxShadow: 4 }}>
      <CardContent>
        <Typography variant="subtitle2" color="text.secondary">
          {post.author ? `u/${post.author} • ` : ""}
          {new Date(post.scraped_at).toLocaleString()}
        </Typography>

        <Typography variant="h6" gutterBottom>
          {post.title}
        </Typography>

        {post.tag && (
          <Chip label={post.tag} size="small" sx={{ mb: 1 }} />
        )}

        <Box mt={1}>
          <Link
            href={post.link}
            target="_blank"
            rel="noopener noreferrer"
            underline="hover"
            sx={{ wordBreak: "break-word", color: "primary.main" }}
          >
            {post.link}
          </Link>
        </Box>

        {/* Use Reddit embed for native playback support */}
        {post.is_reddit_video && redditEmbedUrl ? (
          <Box mt={2}>
            <iframe
              src={redditEmbedUrl}
              style={{
                width: "100%",
                height: "480px",
                border: "none",
                borderRadius: 8,
                overflow: "hidden"
              }}
              scrolling="no"
              allow="autoplay; fullscreen"
              title="Reddit video"
            ></iframe>
          </Box>
        ) : post.video_url ? (
          <Box mt={2}>
            <video
              controls
              style={{ width: "100%", borderRadius: 8 }}
              crossOrigin="anonymous"
            >
              <source src={post.video_url} type="video/mp4" />
              Your browser does not support the video tag.
            </video>
          </Box>
        ) : post.image_url ? (
          <Box mt={2}>
            <img
              src={post.image_url}
              alt={post.title || "Reddit post image"}
              style={{
                width: "100%",
                maxHeight: 400,
                objectFit: "cover",
                borderRadius: 8,
              }}
              referrerPolicy="no-referrer"
            />
          </Box>
        ) : null}
      </CardContent>
    </Card>
  );
}
